import React, { useState, useEffect } from 'react';
import axios from 'axios';

function UsersTable() {
  const [users, setUsers] = useState([]);
  const [selectedUsers, setSelectedUsers] = useState([]);

  //const date = new Date();

  useEffect(() => {
    // Загрузка пользователей при монтировании компонента
    fetchUsers();
  }, []);

  const fetchUsers = async () => {
    const response = await axios.get('http://localhost:3001/api/users');
    setUsers(response.data);
  };

  const handleCheckboxChange = (userId) => {
    setSelectedUsers((prevSelected) => {
      if (prevSelected.includes(userId)) {
        return prevSelected.filter((id) => id !== userId);
      } else {
        return [...prevSelected, userId];
      }
    });
  };

  const handleAction = async (action) => {
    // Обработка действий (Block, Unblock, Delete)
    // ...
  };

  return (
    <div className="container mt-5">
      <div className="btn-group mb-3" role="group">
        <button type="button" className="btn btn-danger mr-2" onClick={() => handleAction('block')}>
          <i className="bi bi-x-circle mr-2"></i> Block
        </button>
        <button type="button" className="btn btn-success mr-2" onClick={() => handleAction('unblock')}>
          <i className="bi bi-check-circle"></i> Unblock
        </button>
        <button type="button" className="btn btn-warning" onClick={() => handleAction('delete')}>
          <i className="bi bi-trash"></i> Delete
        </button>
      </div>
      <table className="table">
        <thead>
          <tr>
            <th scope="col">
              <input type="checkbox" />
            </th>
            <th scope="col">Name</th>
            <th scope="col">Email</th>
            <th scope="col">Last Login</th>
            <th scope="col">Status</th>
          </tr>
        </thead>
        <tbody>
          {users.map((user) => (
            <tr key={user.id}>
              <td>
                <input
                  type="checkbox"
                  checked={selectedUsers.includes(user.id)}
                  onChange={() => handleCheckboxChange(user.id)}
                />
              </td>
              <td>{user.name}</td>
              <td>{user.email}</td>
              <td>{user.registration_date}</td>
              <td>{user.status}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default UsersTable;
