import React, { useState } from 'react';
import { auth, createUserWithEmailAndPassword } from '../firebase';

function Register() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState(null);

  const handleRegister = async (e) => {
    e.preventDefault();

    const handleRegister = async (e) => {
      e.preventDefault();

      try {
        await createUserWithEmailAndPassword(auth, email, password); // Используйте email и password из состояний
        // Перенаправление пользователя на нужную страницу после регистрации
      } catch (err) {
        setError(err.message);
      }
    };

    return (
      <div className="container mt-5">
        <h2 className="text-center mb-4">Register</h2>
        <form onSubmit={handleRegister} className="needs-validation" noValidate>
          <div className="mb-3">
            <label htmlFor="email" className="form-label">Email:</label>
            <input
              type="email"
              className="form-control"
              id="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
            />
          </div>
          <div className="mb-3">
            <label htmlFor="password" className="form-label">Password:</label>
            <input
              type="password"
              className="form-control"
              id="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
            />
          </div>
          <button type="submit" className="btn btn-primary">Register</button>
        </form>
        {error && <p className="text-danger mt-3">{error}</p>}
      </div>

    );
  };
};

export default Register;
